INSERT INTO scenario (id, title, url, description, content, created_at, updated_at)
VALUES ('kaxkv1', 'Flom','' , 'Forbered deg på flom og lær hva du bør gjøre før, under og etter vannet stiger.', 'Flom kan oppstå plutselig ved mye regn, snøsmelting eller ekstremvær. Sørg for at eiendommen din er sikret, og at du har tilgang på rent vann og nødvendige forsyninger. Under flom må du unngå oversvømte områder og følge myndighetenes råd. Etter flom bør du være obs på forurenset vann og elektriske farer.', NOW(), NOW()),
       ('bswie0', 'Atomulykke','' , 'Vet du hva du skal gjøre ved en atomulykke? Beskytt deg effektivt.', 'Ved en atomulykke er det viktig å handle raskt. Gå innendørs, steng dører og vinduer, og følg med på myndighetenes varsler. Unngå eksponering for radioaktivt støv, og ta jodtabletter ved beskjed. Ikke forlat hjemmet før du får klarsignal fra beredskapsmyndigheter.', NOW(), NOW()),
       ('xbcaz6', 'Matmangel','' , 'Få tips til hvordan du sikrer matforsyning i krisetider.', 'Matmangel kan komme plutselig grunnet forsyningsbrudd, krig, ekstremvær eller økonomisk uro. Bygg opp et nødlager med langtidsholdbar mat, og lær deg grunnleggende matlagring og konservering. Planlegg måltider og unngå matsvinn. Samarbeid lokalt for å dele ressurser i langvarige kriser.', NOW(), NOW()),
       ('7ty2xp', 'Vannmangel','' , 'Lær hvordan du takler perioder med lite eller ingen tilgang på rent vann.', 'Vannmangel kan skyldes tørke, forurensning eller skader på vannsystemer. Ha alltid et reservelager med drikkevann og lær hvordan du kan rense vann. Begrens vannbruk, og prioriter drikke og hygiene. Ved langvarig vannmangel må man samarbeide med lokalsamfunnet og følge råd fra myndigheter.', NOW(), NOW());